  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'doc7',
	title: 'Automated hyperparameter selection and optimization'
};
export const metadata = {
  "unversionedId": "doc7",
  "id": "doc7",
  "isDocsHomePage": false,
  "title": "Automated hyperparameter selection and optimization",
  "description": "MMMs are likely to contain high cardinality of parameters, ie. alphas, thetas and gammas for geometric ad stock transformation. In addition, parameters dimensionality increases proportionally with the total number of marketing channels to be measured. Thus, it is extremely necessary to deal with a high dimensionality parameter space where, the greater the number of parameters, the greater the model complexity and computational requirements. In order to achieve computational efficiency while maintaining overall model accuracy, we utilize a set of techniques and algorithms:",
  "source": "@site/docs/doc7.md",
